import React from "react";
import { motion } from "framer-motion";
// import aboutImg from "./about.png"; // replace with your image path
import { LightningBoltIcon, CodeIcon, UserGroupIcon, StarIcon  } from '@heroicons/react/outline';

const About = () => {
    const features = [
        {
          name: "Fast Performance",
          description:
            "Experience lightning-fast load times and smooth performance across all devices.",
          icon: BoltIcon,
        },
        {
          name: "Achievements",
          description:
            "We’ve achieved multiple milestones that showcase our excellence and growth.",
          icon: TrophyIcon,
        },
        {
          name: "Trusted Quality",
          description:
            "Our commitment to quality and reliability ensures a trusted experience.",
          icon: StarIcon,
        },
      ];
  return (
    <section className="relative bg-gradient-to-r from-purple-900 via-purple-800 to-purple-700 py-16 px-6 lg:px-20">
      <div className="max-w-7xl mx-auto grid lg:grid-cols-2 gap-12 items-center">
        
        {/* Left Image with Animation */}
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <img
            src="https://hebbkx1anhila5yf.public.blob.vercel-storage.com/attachments/gen-images/public/laptop-showing-code-editor-with-programming-interf-6BTjeHLHPT33O6H0K0jP0d8K6OqrhF.jpg"
            alt="About Program"
            className="rounded-2xl shadow-2xl"
          />
        </motion.div>

        {/* Right Content */}
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-white"
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Why Choose Our <span className="text-blue-300">SDET Program?</span>
          </h2>
          <p className="text-lg mb-8 text-gray-200">
            Our comprehensive program is designed by industry experts to provide
            hands-on experience with the latest testing tools and methodologies
            used by top tech companies.
          </p>

          <div className="space-y-6">
            {features.map((item, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.2 }}
                viewport={{ once: true }}
                className="flex items-start space-x-4"
              >
                <div className="p-3 bg-white/10 rounded-xl">{item.icon}</div>
                <div>
                  <h3 className="text-xl font-semibold">{item.title}</h3>
                  <p className="text-gray-300">{item.desc}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
